  font-size: 1.481vh;
  line-height: 1;
  border: none;
  outline: none;
  color: #fff;
  border-radius: 0.556vh;
  position: relative;
  z-index: 1;
  cursor: pointer;
  margin: 0.37vh;
  &:after {
    content: "";
    display: block;
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    z-index: -1;
    border-radius: 0.833vh;
  }
  &:hover {
    &:after {
      transition: 0.15s;
      top: -0.4vh;
      right: -0.4vh;
      bottom: -0.4vh;
      left: -0.4vh;
    }
  }
  &-icon {
    margin-right: 1.1vh;
  }
}

.green {
  background: #5abf52;
  &:after {
    background: rgba(90, 191, 82, 0.4);
  }
}
.purple {
  background: rgba(132, 93, 205, 1);
  &:after {
    background: rgba(132, 93, 205, 0.4);
  }
}

