            letter-spacing: @2px;
            text-transform: uppercase;
            color: rgba(255, 255, 255, 0.5);

            &:nth-child(2) {
                position: absolute;
                transform-origin: right bottom;
                text-align: right;
                transform: rotate(90deg);
                bottom: -@6px;
                right: @11px;
            }
        }

        .drift-grid {
            position: relative;
            width: @200px - @18px;
            height: @200px - @18px;
            margin-right: @18px;

            img {
                width: 100%;
            }

            .drift-mod-regulator {
                width: @18px;
                min-width: @18px;
                height: @18px;
                min-height: @18px;
                border-radius: 50%;
                position: absolute;
                transform: translate(-50%, 50%);
                background: #FFFFFF;
                box-shadow: @6px @6px @8px rgba(0, 0, 0, 0.45), inset -@2px -@2px @5px rgba(0, 0, 0, 0.65);
            }
        }
    }
}

.driftmod-box .switch-wrapper.flex-line {
    justify-content: flex-start;
}

.driftmod-box {
    z-index: 99999;

    .switch-wrap {
        & + p {
            font-size: @13px;
        }

        input[type=checkbox] {
            display: none;
        }

        label {
            cursor: pointer;
            width: @40px;
            min-width: @40px;
            height: @26px;
            background: rgba(255, 255, 255, 0.05);
            border: @1px solid rgba(255, 255, 255, 0.3);
            display: block;
            border-radius: @100px;
            position: relative;
        }

        label:after {
            content: '';
            position: absolute;
            top: 50%;
            transform: translateY(-50%);
            left: @5px;
            width: @16px;
            height: @16px;
            background: #fff;
            box-shadow: 0px 0px @20px rgba(255, 255, 255, 0.4);
            border-radius: @500px;
            transition: 0.3s;
        }

        input:not(:checked) + label {
            &:after {
                background: rgba(255, 255, 255, 0.05);
                border: @1px solid rgba(255, 255, 255, 0.3);
                box-shadow: 0px 0px @20px rgba(255, 255, 255, 0);
            }
        }

        input:checked + label {
            border: @1px solid rgba(255, 255, 255, 0.6);

            &:after {
                left: calc(~"100% - 0.2125vw");
                transform: translate(-100%, -50%);
            }
        }

        label:active:after {
            width: @24px;

        }
    }
}

.driftmod-box-close-button {
    position: absolute;
    left: 50%;
    top: 0;
    transform: translate(-50%,-120%);
    border-radius: 50%;
    background: rgba(21,21,21, 0.95);
    width: @32px;
    min-width: @32px;
    height: @32px;
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;
    user-select: none;
    .anim;
    img {
        width: @16px;
        height: @16px;
        opacity: .4;
        .anim;
    }
    &:hover {
        transform: translate(-50%,-120%) rotate(90deg);
        img {
            opacity: .8;
        }
    }
    &:active {
        img {
            transform: scale(0.8,0.8);
        }
    }
}


